import 'package:flutter/material.dart';
import 'models/book.dart';
import 'screens/reader_screen.dart';

void main() {
  runApp(BookLibraryApp());
}

class BookLibraryApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Book Library & Reader',
      theme: ThemeData(
        primarySwatch: Colors.blue,
      ),
      home: HomeScreen(),
      debugShowCheckedModeBanner: false,
    );
  }
}

class HomeScreen extends StatefulWidget {
  @override
  _HomeScreenState createState() => _HomeScreenState();
}

class _HomeScreenState extends State<HomeScreen> {
  Book? selectedBook;

  // Sample book list organized by folders (e.g., genres)
  final Map<String, List<Book>> booksByGenre = {
    'Fiction': [
      Book(title: 'Moby Dick', author: 'Herman Melville', filePath: 'path/to/file', genre: 'Fiction'),
      Book(title: 'The Great Gatsby', author: 'F. Scott Fitzgerald', filePath: 'path/to/file', genre: 'Fiction'),
    ],
    'Dystopian': [
      Book(title: '1984', author: 'George Orwell', filePath: 'path/to/file', genre: 'Dystopian'),
      Book(title: 'Brave New World', author: 'Aldous Huxley', filePath: 'path/to/file', genre: 'Dystopian'),
    ],
  };

  void onBookSelected(Book book) {
    setState(() {
      selectedBook = book;
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('Book Library & Reader'),
      ),
      body: Row(
        children: [
          // Sidebar for Book Navigation
          Container(
            width: 300, // Sidebar width
            color: Colors.grey[200],
            child: ListView(
              children: booksByGenre.entries.map((entry) {
                return ExpansionTile(
                  title: Text(entry.key, style: TextStyle(fontWeight: FontWeight.bold)),
                  children: entry.value.map((book) {
                    return ListTile(
                      title: Text(book.title),
                      onTap: () {
                        onBookSelected(book);
                      },
                    );
                  }).toList(),
                );
              }).toList(),
            ),
          ),
          // Content Area for Book Reader
          Expanded(
            child: selectedBook != null
                ? ReaderScreen(book: selectedBook!)
                : Center(
                    child: Text(
                      'Select a book from the sidebar to start reading',
                      style: TextStyle(fontSize: 18, color: Colors.grey[600]),
                    ),
                  ),
          ),
        ],
      ),
    );
  }
}
